<template>
 
      <div class="slideBox" ref="slideBox" @touchstart.stop.prevent="touchstart" @touchmove.stop.prevent="touchmove" @touchend.stop.prevent="touchend">
          {{slidingPrompt}}
          <div class="slideBg" :style="{width: slideWidth + 'px'}">{{isSlide?'': successfulTip}}<div ref="slideSd" :class="slideState?'handler_ok_bg':'handler_bg'"></div></div>
      </div>
</template>
<script>
export default {
  data() {
    return {
      isSlide: true,
      slideState: false,
      slideWidth: '',
      distance: 0,
    };
  },
  props:{
      slidingPrompt:{
          type: String,
          default: '滑动发送验证码'
      },
      successfulTip:{
          type: String,
          default: '验证成功已发送短信'
      },
      uri: {
          type: String,
          default: '',//验证成功调的验证码接口
      }
  },
  methods: {
    touchstart(v){
      if(this.isSlide) this.distance = v.changedTouches[0].clientX
    },
    touchmove(v){
      if(this.isSlide) {
        let a = v.changedTouches[0].clientX - this.distance
        if(a <= this.$refs.slideBox.offsetWidth - this.$refs.slideSd.offsetWidth) this.slideWidth = a
      }
    },
    touchend(v){
      if((v.changedTouches[0].clientX - this.distance) > (this.$refs.slideBox.offsetWidth - this.$refs.slideSd.offsetWidth)) {
        this.slideWidth = this.$refs.slideBox.offsetWidth - this.$refs.slideSd.offsetWidth
        this.slideState = true
        this.isSlide = false
        this.$emit('slideState',this.slideState) // 成功后的抛出的值
      }else{
        this.slideWidth = 0
        this.slideState = false
        this.isSlide = true
      }
    }
  }
};
</script>

<style scoped>
/* 滑动验证 */
.slideBox{
  position: relative;
  text-align: center;
  height: 2rem;
  line-height: 2rem;
  background: #dee5f2;
  touch-action: none;
  font-size: .8rem;
}
.slideBg{
  background: #33FF66;
  position: absolute;
  left: 0;
  top: 0;
  height: 2rem;
}
.slideBg>div{
  position: absolute;
  width: 2rem;
  height: 2rem;
  right: -2rem;
  z-index: 9999;
  top: 0;
  border: 1px solid #ccc;
  box-sizing: border-box;
}
.handler_bg{
    background: #fff url("data:image/png;base64,") no-repeat center;
}
.handler_ok_bg{
    background: #fff url("data:image/png;base64,") no-repeat center;
}
.slideBg{
  text-align: center;
  line-height: 2rem;
  font-size: .8rem;
  color: #fff;
}
</style>
